mapper.select:
        select
        
        from sys_user where status!=9
        
            and `id` = #{id}
        
        
            and `username` = #{username}
        

        
            and `username` like "%"#{usernameLike}"%"
        
        
            and `password` = #{password}
        
        
            and `password` like "%"#{passwordLike}"%"
        
        
            and `nkname` = #{nkname}
        
        
            and `nkname` like "%"#{nknameLike}"%"
        
        
            and `type` = #{type}
        
        
            and `status` = #{status}
        
        
            and `email` = #{email}
        
        
            and `email` like "%"#{emailLike}"%"
        
        
            and `telno` = #{telno}
        
        
            and `telno` like "%"#{telnoLike}"%"
        
        
            and `idcard` = #{idcard}
        
        
            and `idcard` like "%"#{idcardLike}"%"
        
        
            and `sex` = #{sex}
        
        
            and `birth` = #{birth}
        
        
            and `integral` = #{integral}
        
        
            and `address` = #{address}
        
        
            and `address` like "%"#{addressLike}"%"
        
        
            and `wechat` = #{wechat}
        
        
            and `wechat` like "%"#{wechatLike}"%"
        
        
            and `qq` = #{qq}
        
        
            and `face` = #{face}
        
        
            and `face` like "%"#{faceLike}"%"
        
        
            and `remark` = #{remark}
        
        
            and `remark` like "%"#{remarkLike}"%"
        
        
            and `create_time` = #{createTime}
        
        
            and `create_time` >= #{createTimeBegin}
        
        
            and `create_time` <= #{createTimeEnd}
        

        
            and `update_time` = #{updateTime}
        
        
            and `update_time` >= #{updateTimeBegin}
        
        
            and `update_time` <= #{updateTimeEnd}
        
        
            and `province` = #{province}
        
        
            and `city` = #{city}
        
        
            and `county` = #{county}
        
        
            and `account` = #{account}
        
        
            and sys_user.`outId`  = #{outId}

